import React, { Component } from 'react'
import "./App.css"
export default class App extends Component {
  constructor(props){
    super(props)
    this.state ={
      currentClass : "black"
    }
  }
  toggleclass = ()=>{
    this.setState({isClasActive:!this.state.isClasActive})
  }
  render() {
    return (
      <div>
        <h1 className={this.state.currentClass}>Adfar Rasheed</h1>
        <button onClick={this.rest}>Reset</button>
        <button onClick={this.toggleclass}>Green</button>
        <button onClick={this.toggleclass}>Blue</button>
        <button onClick={this.toggleclass}>Red</button>
        <button onClick={this.toggleclass}>Yellow</button>
      </div>
    )
  }
}
