import React, { Component } from 'react'
import "./App.css"
export default class App extends Component {
  constructor(props){
    super(props)
    this.state ={
      currentClass : "black"
    }
  }
  green=()=>{
    this.setState({currentClass:"green"})
  }
  blue=()=>{
    this.setState({currentClass:"blue"})
  }
  red=()=>{
    this.setState({currentClass:"red"})
  }
  yellow=()=>{
    this.setState({currentClass:"yellow"})
  }
  render() {
    return (
      <div>
        <h1 className={this.state.currentClass}>Adfar Rasheed</h1>
        <button onClick={this.reset}>Reset</button>
        <button onClick={this.green}>Green</button>
        <button onClick={this.blue}>Blue</button>
        <button onClick={this.red}>Red</button>
        <button onClick={this.yellow}>Yellow</button>
      </div>
    )
  }
}
